import { authTypes } from './types';

const { AUTH_ACTION_FAILED, SIGNIN, AUTH_LOADING } = authTypes;

export const verifyAuth = () => async (dispatch) => {
  try {
    const res = await fetch('http://localhost:5000/api/auth/verify');
    dispatch({ type: SIGNIN, payload: (await res.json()).user });
  } catch (err) {
    dispatch({
      type: AUTH_ACTION_FAILED,
      payload: err
    });
  }
};

export const login = (credentials) => async (dispatch) => {
  try {
    const res = await fetch('http://localhost:5000/api/auth/login', {
      method: 'POST',
      body: JSON.stringify(credentials),
      headers: { 'Content-Type': 'application/json' }
    });
    const data = await res.json();

    if (data.errors) {
      dispatch({ type: AUTH_ACTION_FAILED, payload: data.errors });
    }
    if (data.user) {
      dispatch({ type: SIGNIN, payload: data.user });
    }
  } catch (err) {
    dispatch({ type: AUTH_ACTION_FAILED, payload: err });
  }
};
// export const logouot = () => (dispatch, getState) => {};
export const signup = ({ email, password, userName }) => async (dispatch) => {
  try {
    const res = await fetch('http://localhost:5000/api/auth/signup', {
      method: 'POST',
      body: JSON.stringify({ email, password, userName }),
      headers: { 'Content-Type': 'application/json' }
    });
    const data = await res.json();

    if (data.errors) {
      dispatch({ type: AUTH_ACTION_FAILED, payload: data.errors });
    }
    if (data.user) {
      dispatch({ type: SIGNIN, payload: data.user });
    }
  } catch (err) {
    dispatch({ type: AUTH_ACTION_FAILED, payload: err });
  }
};
