import { urlTypes } from '../actions/types';

const {
  SHORTEN_ERROR,
  SHORTEN_LOADING,
  SHORTEN_URL_SUCCESS,
  FETCH_URLS_SUCCESS,
  DELETE_URL_SUCCESS
} = urlTypes;

const initialState = {
  urls: null,
  loading: false,
  error: null,
  currentUrl
};

const urlReducer = (state = initialState, action) => {
  switch (action.type) {
    case DELETE_URL_SUCCESS: {
      return {
        ...state,
        urls: action.payload,
        loading: false,
        error: null
      };
    }
    case FETCH_URLS_SUCCESS: {
      return {
        ...state,
        error: null,
        loading: null,
        urls: action.payload
      };
    }
    case SHORTEN_URL_SUCCESS: {
      return {
        ...state,
        loading: false,
        currentUrl: action.payload,
        error: null
      };
    }
    case SHORTEN_ERROR: {
      return {
        ...state,
        error: action.payload,
        loading: false
      };
    }
    case SHORTEN_LOADING: {
      return {
        ...state,
        loading: true
      };
    }
    default:
      return state;
  }
};

export default urlReducer;
