import React from 'react';
import { useSelector, useDispatch } from 'react-redux';

import Navigation from '../components/Navigation';
import { authSelectors } from '../store/selectors';
import { authActions } from '../store/actions';
import UserIcon from '../components/UserIcon';
import Form from '../components/Form';
import URL from '../constants';

const Home = () => {
  const dispatch = useDispatch();
  const authToken = useSelector(authSelectors.selectAuthToken);
  const [values, setValues] = React.useState({});

  const handleSubmit = React.useCallback(
    (e) => {
      e.preventDefault();

      dispatch(
        authActions.signup({
          email,
          password,
          userName
        })
      );
    },
    [values]
  );

  const Logout = React.useCallback(async () => {
    dispatch(authActions.logout());
  }, [dispatch, authActions]);
  return (
    <>
      <Navigation
        Title="URL-shortener"
        Links={[
          {
            title: 'Login',
            ref: '/login',
            condition: !!authToken,
            replacement: (
              <UserIcon
                Actions={[
                  { name: 'Dashboard', act: () => {} },
                  { name: 'Logout', act: Logout }
                ]}
                Letter={authToken ? authToken.userName[0] : null}
              />
            )
          }
        ]}
      />

      <Form
        Title="Shorten your URL"
        ParentState={[values, setValues]}
        SubmitFunction={handleSubmit}
        Inputs={[
          { label: 'URL', id: 'url', type: 'text' },
          {
            label: 'custom',
            id: 'custom',
            type: 'text',

            exteriorLabel: `your custom path`
          }
        ]}
        LowerText={{
          info: `${URL}${values.custom || 'your custom path'}`,
          link: {}
        }}
      />
    </>
  );
};

export default Home;
