import { createSelector } from 'reselect';

const selectAuth = (state) => state.auth;

export const selectAuthToken = createSelector(selectAuth, (auth) => {
  return auth.authToken;
});

export const selectError = createSelector(selectAuth, (auth) => {
  return auth.error;
});

export const selectCurrentUrl = createSelector(selectAuth, (auth) => {
  return auth.currentUrl;
});

export const selectLoading = createSelector(selectAuth, (auth) => {
  return auth.loading;
});
